mapper.update:
update address_book
consignee = #{consignee}, building = #{building}, specific_location = #{specificLocation}, is_default = #{isDefault},
where id = #{id}

mapper.select:
select * from address_book 

    and id = #{id}
    and userId = #{userId}
    and consignee = #{consignee}
    and building = #{building}
    and specific_location = #{specificLocation}
    and is_default = #{isDefault}
